catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Create the variables dateNeeded and dateReturnVehicle.
   var dateNeeded = g_form.getValue('date_needed');
   var dateReturnVehicle = g_form.getValue('date_return_vehicle');

   //If the return date is before the date needed clear date selection and display error message.
   if (dateReturnVehicle < dateNeeded){
	g_form.clearValue('date_return_vehicle');
	g_form.showFieldMsg('date_return_vehicle','Date to return vehicle should not occur before the date needed','error');

	return false; // Prevents the submition of the form.

   }

   else {
	g_form.hideAllFieldMsgs();
	return true; //Allows the submission of the form.
   }

}
