sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var loaVehTrk = (current.vehicle_tracker); // Get the value of the vehicle_tracker field from the current record and assigns to loaVehTrk
    if (loaVehTrk) { // Check if the vehicle_tracker field is populated
        var newLoaVehTrk = new GlideRecord('x_1134503_lva_vehicle_tracker'); // Initialize a new GlideRecord for the vehicle_tracker table
        if (newLoaVehTrk.get(loaVehTrk)) {// Query the vehicle_tracker table using the value from the current record
            newLoaVehTrk.setValue('vehicle_status', current.vehicle_status);// Set the vehicle_status field on the queried record to the current record's vehicle_status
            newLoaVehTrk.setValue('tracker_status', 'Closed');//Set the value of the tracker status field to 'Closed'

            newLoaVehTrk.update();// Update the record in the vehicle_tracker table
        }
    }
})(current, previous);
