sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    // Ensure the script only runs if the vehicle status changes to 'Sent for Servicing'


        // Create a new GlideRecord object for the 'loaner_vehicle_servicing' table
        var loaVehService = new GlideRecord('x_1134503_lva_vehicle_servicing');


        loaVehService.addQuery('vehicle_tracker', current.sys_id);//addquery to filter records with an open ticket for vehicle tracker on service table
        loaVehService.addQuery('tracker_status', 'Open');// addquery servicing table to filter records with tracker status is open
        loaVehService.query();//execute the query on servicing table based on addquery filters

        //if no open tickets exists, create a new one
        if (!loaVehService.next()) {
			var newLoaVehService = new GlideRecord('x_1134503_lva_vehicle_servicing');
			var todayDate = new GlideDate();
            newLoaVehService.initialize(); //creates an empty servicing record suitable for population before an insert

            //Map fields from vehicle tracker to the new vehicle servicing ticket
            newLoaVehService.vehicle_tracker = current.sys_id;
            newLoaVehService.vehicle = current.vehicle;
            newLoaVehService.vehicle_status = 'In Service'; // set the vehicle status to in service
			newLoaVehService.date_sent_for_servicing = todayDate.getValue();//set current date and time
            newLoaVehService.ticket_status = 'Open'; //set the ticket status to open

            //insert a new record using the field values that have been set for servicing record
            newLoaVehService.insert();
        }




})(current, previous);
