sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // // Add your code here
    var currentVehStatus = current.vehicle_status; //creates variable for current vehicle status


    // Create a new GlideRecord object for the 'loaner_vehicle_catalog' table
    var loaVehCatalog = new GlideRecord('x_1134503_lva_loaner_vehicle_catalog');

    // Add a query to find the record in 'loaner_vehicle_catalog' where 'sys_id' matches the 'vehicle' field in the current Vehicle Tracker record
    loaVehCatalog.addQuery('sys_id', current.vehicle);

    // Execute the query
    loaVehCatalog.query();

    // Iterate through the records returned by the query
    if (loaVehCatalog.next()) {

        // Check if the vehicle status is 'Pending Release' or 'Out on Field' or 'Returned for Inspection' or 'Sent for Servicing'
        if (currentVehStatus == 'Pending Release' ||
            currentVehStatus == 'Out on Field' ||
            currentVehStatus == 'Returned for Inspection' ||
            currentVehStatus == 'Sent for Servicing') {

            // If any of these conditions are true, set the 'status' field of the vehicle record to 'Unavailable'
            loaVehCatalog.setValue('status', 'Unavailable');
        }
        // If the vehicle status is 'Returned to Warehouse', set the 'status' field to 'Available'
        else if (currentVehStatus == 'Returned to Warehouse') {
            loaVehCatalog.setValue('status', 'Available');
        }
        // If the vehicle status is 'Decommissioned', set the 'status' field to 'Decommissioned'
        else if (currentVehStatus == 'Decommissioned') {
            loaVehCatalog.setValue('status', 'Decommissioned');
        }

        // Save the changes made to the vehicle record
        loaVehCatalog.update();
    }


})(current, previous);
