sys_ui_action.script:
(function executeAction() {
    // Define the template text with HTML tags for formatting
    var templateText =
        "<h2>Overview:</h2>" +
        "<p>Provide a brief description of the vehicle's purpose and key characteristics.</p>" +

        "<h2>Features:</h2>" +
        "<ul>" +
        "<li><b>Feature 1:</b> Describe feature 1</li>" +
        "<li><b>Feature 2:</b> Describe feature 2</li>" +
        "<li><b>Feature 3:</b> Describe feature 3</li>" +
        "</ul>" +

        "<h2>Recommended Uses:</h2>" +
        "<p>List the main use cases and scenarios where this vehicle would be best suited.</p>" +

        "<h2>Additional Information:</h2>" +
        "<ul>" +
        "<li><b>Payload capacity:</b> Enter the payload capacity</li>" +
        "<li><b>Towing capacity:</b> Enter the towing capacity</li>" +
        "<li><b>Fuel efficiency:</b> Optional, enter fuel details or leave blank if not applicable.</li>" +
        "</ul>";

    // Set the template text into the article body field
    current.text = templateText;

    // Notify the user that the template has been inserted
    gs.addInfoMessage("Vehicle Description Template inserted.");

    // Optional: Updates the record without redirecting
    current.update();
})();
